import React, { useRef, useEffect, useState, useCallback } from "react";
import { useGlobalState } from "../context/globalContext";
import mojs from "@mojs/core";
const Radiation = ({ earthRef }) => {
  const animDom = useRef();
  const ring1 = useRef();
  const ring2 = useRef();
  const ring3 = useRef();
  const ring4 = useRef();
  const ring5 = useRef();
  const [isOpen, setIsOpen] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  const {
    radiationDuration,
    radiationAmplitude,
    radiationRadius,
    radiationPosition,
  } = useGlobalState();

  useEffect(() => {
    if (animDom && animDom.current) {
      ring1.current = new mojs.Shape({
        parent: animDom.current,
        shape: "circle",
        fill: "none",
        scale: { 0: 1 },

        radius: { 0: Math.floor(radiationRadius / 3) },
        strokeWidth: { 30: 0 },
        stroke: "#E96BFF",
        delay: "rand(75, 150)",

        angle: { "rand(-35, -70)": 0 },
        duration: Math.floor(3000 / radiationAmplitude),
        left: 0,
        top: 0,
        easing: "cubic.inout",
        className: "no-pointer",
        repeat: Math.floor(
          radiationDuration / Math.floor(3000 / radiationAmplitude)
        ),
      });

      ring2.current = new mojs.Shape({
        parent: animDom.current,
        shape: "circle",
        fill: "none",
        scale: { 0: 1 },

        radius: { 0: Math.floor((2 * radiationRadius) / 3) },
        strokeWidth: { 15: 0 },
        stroke: "#F57657",
        delay: "rand(75, 150)",

        angle: { "rand(-35, -70)": 0 },
        duration: Math.floor(3000 / radiationAmplitude),
        left: 0,
        top: 0,
        easing: "cubic.inout",
        className: "no-pointer",
        repeat: Math.floor(
          radiationDuration / Math.floor(3000 / radiationAmplitude)
        ),
      });

      ring3.current = new mojs.Shape({
        parent: animDom.current,
        shape: "circle",
        fill: "none",
        scale: { 0: 1 },

        radius: { 0: radiationRadius },
        strokeWidth: { 10: 0 },
        stroke: "#F57657",
        delay: "rand(75, 150)",

        angle: { "rand(-35, -70)": 0 },
        duration: Math.floor(3000 / radiationAmplitude),
        left: 0,
        top: 0,
        easing: "cubic.inout",
        className: "no-pointer",
        repeat: Math.floor(
          radiationDuration / Math.floor(3000 / radiationAmplitude)
        ),
      });
      ring4.current = new mojs.Shape({
        parent: animDom.current,
        shape: "circle",
        fill: "none",
        scale: { 0: 1 },

        radius: { 0: radiationRadius / 4 },
        strokeWidth: { 10: 0 },
        stroke: "#F57657",
        delay: "rand(75, 150)",

        angle: { "rand(-35, -70)": 0 },
        duration: Math.floor(3000 / radiationAmplitude),
        left: 0,
        top: 0,
        easing: "cubic.inout",
        className: "no-pointer",
        repeat: Math.floor(
          radiationDuration / Math.floor(3000 / radiationAmplitude)
        ),
      });
    }
  }, [animDom, radiationDuration, radiationAmplitude, radiationRadius]);

  useEffect(() => {
    if (radiationPosition) {
      ring1.current
        .tune({ x: radiationPosition.x, y: radiationPosition.y })
        .play();
      ring2.current
        .tune({ x: radiationPosition.x, y: radiationPosition.y })
        .play();
      ring3.current
        .tune({ x: radiationPosition.x, y: radiationPosition.y })
        .play();
      ring4.current
        .tune({ x: radiationPosition.x, y: radiationPosition.y })
        .play();
    }
    // console.log(radiationPosition);
  }, [radiationPosition]);

  return (
    <div
      ref={animDom}
      className="Radiation"
      style={{ position: "absolute" }}
    ></div>
  );
};

export default Radiation;
