import React, { useRef, useEffect, useState, useCallback } from "react";
import { useGlobalState } from "../context/globalContext";
import animationData from "../assets/volcana.json";
import mojs from "@mojs/core";
import Lottie from "react-lottie";
const Volcano = ({ earthRef }) => {
  const { volcanoPosition, volcanoSize } = useGlobalState();

  const defaultOptions = {
    loop: true,
    autoplay: true,
    animationData: animationData,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  const burst = new mojs.Burst({
    radius: { 0: 100 },
    count: 5,
    degree: 30,
    repeat: 5,
    children: {
      fill: { magenta: "cyan" },
      duration: 2000,
    },
  });

  useEffect(() => {
    if (volcanoPosition) {
      burst.tune({ x: volcanoPosition.x, y: volcanoPosition.y }).play();
    }
  }, [volcanoPosition]);
  return (
    <div className="Volcano" style={{ position: "absolute" }}>
      {volcanoPosition ? (
        <div
          className="Volcano"
          style={{
            position: "absolute",
            top: volcanoPosition.y - volcanoSize / 2,
            left: volcanoPosition.x - volcanoSize / 2,
          }}
        >
          <Lottie
            options={defaultOptions}
            height={volcanoSize}
            width={volcanoSize}
            style={{ transition: "all 2s linear" }}
          />
        </div>
      ) : null}
    </div>
  );
};

export default Volcano;
