import mojs from "@mojs/core";

const EarthQuake = ({ e, earthRef }) => {
  const OPTS = {
    parent: earthRef.current,
    fill: "none",
    radius: 25,
    strokeWidth: { 50: 0 },
    scale: { 0: 1 },
    angle: { "rand(-35, -70)": 0 },
    duration: 2000,
    left: 0,
    top: 0,
    easing: "cubic.out",
    className: "no-pointer",
  };

  const circle1 = new mojs.Shape({
    ...OPTS,
    stroke: "cyan",
  });

  const circle2 = new mojs.Shape({
    ...OPTS,
    radius: { 0: 15 },
    strokeWidth: { 30: 0 },
    stroke: "magenta",
    delay: "rand(75, 150)",
  });

  const circle3 = new mojs.Shape({
    ...OPTS,
    radius: { 0: 30 },
    strokeWidth: { 30: 0 },
    stroke: "magenta",
    delay: "rand(75, 150)",
  });

  circle1.tune({ x: e.offsetX, y: e.offsetY }).replay();
  circle2.tune({ x: e.offsetX, y: e.offsetY }).replay();
  circle3.tune({ x: e.offsetX, y: e.offsetY }).replay();
};

export default EarthQuake;
