import { Row, Card } from "react-bootstrap";
import Slider from "@material-ui/core/Slider";
import Typography from "@material-ui/core/Typography";
import Tooltip from "@material-ui/core/Tooltip";
import { useGlobalActions, useGlobalState } from "../context/globalContext";
import { Button } from "react-bootstrap";
function ValueLabelComponent(props) {
  const { children, open, value } = props;

  return (
    <Tooltip open={open} enterTouchDelay={0} placement="top" title={value}>
      {children}
    </Tooltip>
  );
}

const Statisitcs = () => {
  const { setStatus, setSpeed } = useGlobalActions();
  const { status, speed } = useGlobalState();

  return (
    <Card style={{ padding: 4, margin: 4, top: 4, left: 4, width: 300 }}>
      <Row style={{ margin: 0, padding: 4 }}>
        <Button
          variant="outline-dark"
          style={{
            padding: "2px 4px 2px 4px",
            margin: 4,
          }}
          onClick={(e) => {
            e.preventDefault();
            status.localeCompare("Playing") === 0
              ? setStatus("Paused")
              : setStatus("Playing");
          }}
        >
          {status}
        </Button>
        <Button
          variant="outline-dark"
          style={{
            padding: "2px 4px 2px 4px",
            margin: 4,
          }}
          onClick={(e) => {
            e.preventDefault();
            status.localeCompare("Finished") === 0
              ? setStatus("Playing")
              : setStatus("Finished");
          }}
        >
          {status.localeCompare("Finished") === 0
            ? "Start simulation"
            : "Finish"}
        </Button>
      </Row>
      <Row style={{ margin: 0, padding: "4px 8px 4px 8px" }}>
        <Typography gutterBottom>Tooltip value label</Typography>
        <Slider
          ValueLabelComponent={ValueLabelComponent}
          value={speed}
          onChange={(e) => {
            setSpeed(e.target.value);
          }}
        />
      </Row>
    </Card>
  );
};

export default Statisitcs;
