import { Vector } from "./vector";
export class Ball {
  constructor(x, y, r, m, BALLZ) {
    this.pos = new Vector(x, y);
    this.r = r;
    this.m = r ** 2;
    if (this.m === 0) {
      this.inv_m = 0;
    } else {
      this.inv_m = 1 / this.m;
    }
    this.elasticity = 3;
    this.vel = new Vector(0, 0);
    this.acc = new Vector(0, 0);
    this.acceleration = 0;
    this.player = false;
    BALLZ.push(this);
  }

  drawBall(ctx) {
    ctx.beginPath();
    ctx.arc(this.pos.x, this.pos.y, this.r, 0, 2 * Math.PI);
    ctx.fillStyle = "red";
    ctx.fill();
    ctx.closePath();
  }

  display(ctx) {
    // this.vel.drawVec(ctx, 550, 400, 10, "green");
    // this.acc.unit().drawVec(ctx, 550, 400, 50, "blue");
    // ctx.beginPath();
    // ctx.arc(550, 400, 50, 0, 2 * Math.PI);
    // ctx.strokeStyle = "black";
    // ctx.stroke();
    // ctx.closePath();
  }
  reposition(ctx) {
    // console.log(this.pos);
    // console.log(window.innerHeight, window.innerWidth);
    const multiplier = [1, -1];
    // const skipX = Math.random() * 0.5;
    // const skipY = Math.random() * 0.5;
    const plusMinusX = Math.floor(Math.random() * 2);
    const plusMinusY = Math.floor(Math.random() * 2);

    // const newPosition = {
    //   x: this.pos.x + multiplier[plusMinusX] * skipX,
    //   y: this.pos.y + multiplier[plusMinusY] * skipY,
    // };

    // if (newPosition.x + this.r + 10 > window.innerWidth) {
    //   newPosition.x = window.innerWidth - this.r - 10;
    // }
    // if (newPosition.y + this.r + 10 > window.innerHeight) {
    //   newPosition.y = window.innerHeight - this.r - 10;
    // }
    // if (newPosition.x < 10) {
    //   newPosition.x = 10 + this.r;
    // }
    // if (newPosition.y < 10) {
    //   newPosition.y = 10 + this.r;
    // }

    this.acc = this.acc.unit().mult(this.acceleration);

    const speed = 0.5;
    if (Math.random() > 0.7)
      this.vel = this.vel.add(
        new Vector(
          multiplier[plusMinusX] * speed,
          multiplier[plusMinusY] * speed
        )
      );
    this.vel = this.vel.mult(1 - 0.07);
    this.pos = this.pos.add(this.vel);

    // console.log(this.pos);
  }
}
