import React, { createRef, useEffect, useState } from "react";



const GlobalStateContext = React.createContext(undefined);
const GlobalActionsContext = React.createContext(undefined);

function ga(){

}

const GlobalContextProvider = ({ children }) => {









  const [creatures, setCreatures] = useState(Array.from(Array(100).keys()));
  const creatureRef= creatures.map(()=>createRef());


  useEffect(()=>{
    console.log(creatureRef)

    for(var i=0;i<creatureRef.length;i++){
      console.log(creatureRef[i]);
      if (creatureRef[i] && creatureRef[i].current) {
        const handler = (e) => {
          if (e?.detail) {
            const newC1 = e.detail;
            creatures[i] = newC1;
          }
        };
        creatureRef[i].current.addEventListener("position", handler);
        return () =>
          creatureRef[i].current.removeEventListener("position", handler);
      }
    }

    console.log(creatures[0])
    console.log(creatureRef);
  },[creatureRef])


  return (
    <GlobalStateContext.Provider
      value={{
        creatures,
        creatureRef,
      }}
    >
      <GlobalActionsContext.Provider value={{ setCreatures }}>
        {children}
      </GlobalActionsContext.Provider>
    </GlobalStateContext.Provider>
  );
};

const useGlobalState = () => {
  const context = React.useContext(GlobalStateContext);
  if (context === undefined) {
    throw new Error(
      "useGlobalState error"
    );
  }

  return context;
};

const useGlobalActions = () => {
  const context = React.useContext(GlobalActionsContext);
  if (context === undefined) {
    throw new Error(
      "useGlobalActions error"
    );
  }

  return context;
};

export { useGlobalState, useGlobalActions, GlobalContextProvider };
