import React, { useRef, useEffect, useState, useCallback } from "react";
import mojs from "@mojs/core";
const EarthQuake = ({ duration, earthRef }) => {
  const animDom = useRef();
  const bouncyCircle = useRef();
  const [isOpen, setIsOpen] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (animDom && animDom.current) {
      bouncyCircle.current = new mojs.Shape({
        parent: animDom.current,
        shape: "circle",
        fill: "none",
        radius: 25,
        strokeWidth: { 50: 0 },
        scale: { 0: 1 },
        angle: { "rand(-35, -70)": 0 },
        duration: duration,
        easing: "cubic.out",

        radius: { 0: 15 },
        strokeWidth: { 30: 0 },
        stroke: "magenta",
        delay: "rand(75, 150)",
      });
    }
  }, [animDom]);

  // Update the animation values when the prop changes
  useEffect(() => {
    if (earthRef && earthRef.current) {
      console.log("initiate handler of earth quake");
      const handler = (e) => {
        console.log("recieved event ");
        if (e.position) {
          bouncyCircle.current
            .tune({ x: e.position.x, y: e.position.y })
            .replay();
        }
      };
      earthRef.current.addEventListener("earthQuake", handler);
      return () =>
        earthRef?.current?.removeEventListener("earthQuake", handler);
    }
  });

  return <div ref={animDom} className="EarthQuake"></div>;
};

export default EarthQuake;
