import { useEffect, useRef, useState } from "react";
import { Vector } from "./vector";
import { Creature } from "./creature";
import { Wall } from "./wall";
import { useGlobalState, useGlobalActions } from "../context/globalContext";
import { Food } from "./food";
import EarthQuake from "./earthQuake";
import Volcano from "./volcana";
import Radiation from "./radiation";
import WaterBody from "./waterBody";
import {makeInitialFood,makeInitialPopulation} from "./gaFunctions/initiators"
import {isPairEqual,randInt} from "./utils/util";
import {matingPool} from "./gaFunctions/mating";
import {findFood} from "./gaFunctions/survival";

function clearCanvas(ctx, canvasRef) {
  ctx?.clearRect(
    0,
    0,
    canvasRef?.current?.clientWidth,
    canvasRef?.current?.clientHeight
  );
}


function addRandomNumberOfFood(foods) {
  if (Math.random() > 0.8) {
    const food = new Food(
      randInt(10, window.innerWidth - 300 - 10),
      randInt(10, window.innerHeight - 10),
      foods
    );
  }
}




function die(creatures) {
  for (let i = 0; i < creatures.current.length; i++) {
    if (creatures.current[i].canDie()) {
      creatures.current.splice(i, 1);
    }
  }
}




const Earth = () => {
  const canvasRef = useRef(null);
  const [ctx, setCtx] = useState(null);
  const CREATURES = useRef([]);
  const FOODS = useRef([]);
  const localSpeed = useRef(0.5);
  const birthCache = useRef([]);
  const {
    restarted,
    calamityType,
    calamities,
    resources,
    resourceType,
    status,
    speed,
    initialPopulation,
  } = useGlobalState();

  const {
    setTotalPopulation,
    setCalamityPosition,
    setResourcePosition,
  } = useGlobalActions();
  const localStatus = useRef(status);

  useEffect(() => {
    localSpeed.current = speed;
  }, [speed]);
  useEffect(() => {
    if (canvasRef && canvasRef.current) {
      const canvas = document.getElementById("canvas");
      const ctx = canvas.getContext("2d");
      canvasRef.current.focus();
      setCtx(ctx);
    }
  }, [canvasRef]);

  useEffect(() => {
    if (ctx) {
      clearCanvas(ctx, canvasRef);
      makeInitialPopulation(initialPopulation, CREATURES);
      makeInitialFood(FOODS);
    }
  }, [ctx, restarted, initialPopulation]);

  useEffect(() => {
    let reqID = null;
    localStatus.current = status;
    if (status.localeCompare("Playing") === 0) {
      reqID = requestAnimationFrame(mainLoop);
    }
    return () => {
      if (reqID) {
        cancelAnimationFrame(reqID);
      }
    };
  }, [status]);

  function mainLoop(timestamp) {
    if (localStatus.current.localeCompare("Playing") === 0) {
      clearCanvas(ctx, canvasRef);
      findFood(CREATURES, FOODS);
      die(CREATURES);
      // infection(CREATURES);
      matingPool(CREATURES);

      CREATURES.current.forEach((b, index) => {
        b.updateCreature(ctx, localSpeed.current);
      });

      FOODS.current.forEach((f, index) => {
        f.drawFood(ctx);
      });
      addRandomNumberOfFood(FOODS);
      setTotalPopulation(CREATURES.current.length);
      requestAnimationFrame(mainLoop);
    }
    return;
  }

  function handleCanvasClick(e) {
    const mouse = new Vector(e.clientX - 300, e.clientY);
    if (e.button === 0) {
      if (
        calamityType.localeCompare("none") !== 0 &&
        calamities.length < 5 &&
        resourceType.localeCompare("none") === 0
      ) {
        setCalamityPosition({
          x: mouse.x,
          y: mouse.y,
        });
      } else if (
        resourceType.localeCompare("none") !== 0 &&
        resources.length < 5 &&
        calamityType.localeCompare("none") === 0
      ) {
        setResourcePosition({
          x: mouse.x,
          y: mouse.y,
        });
      }
    }
  }
  return (
    <div>
      {
        resources
          ? resources.map((resource) => {
              if (resource.type.localeCompare("waterBody") === 0) {
                return (
                  <WaterBody
                    key={`${resource.id}+resource`}
                    uid={resource.id}
                    size={resource.size}
                    position={resource.position}
                  ></WaterBody>
                );
              }
            })
          : null
      }
      {
        calamities
          ? calamities.map((calamity) => {
              if (calamity.type.localeCompare("earthQuake") === 0) {
                return (
                  <EarthQuake
                    key={`${calamity.id}+calamity`}
                    size={calamity.size}
                    amplitude={calamity.amplitude}
                    duration={calamity.duration}
                    position={calamity.position}
                  />
                );
              } else if (calamity.type.localeCompare("volcano") === 0) {
                return (
                  <Volcano
                    key={`${calamity.id}+calamity`}
                    size={calamity.size}
                    amplitude={calamity.amplitude}
                    duration={calamity.duration}
                    position={calamity.position}
                  />
                );
              } else if (calamity.type.localeCompare("radiation") === 0) {
                return (
                  <Radiation
                    key={`${calamity.id}+calamity`}
                    size={calamity.size}
                    amplitude={calamity.amplitude}
                    duration={calamity.duration}
                    position={calamity.position}
                  />
                );
              }
            })
          : null
      }
      <canvas
        onClick={handleCanvasClick}
        tabIndex={1}
        id="canvas"
        ref={canvasRef}
        width={window.innerWidth - 300}
        height={window.innerHeight}
        style={{ backgroundColor: "#222222" }}
      ></canvas>
    </div>
  );
};

export default Earth;
