import { createRef, useEffect, useRef, useState } from "react";
import { Card ,Row,Col} from "react-bootstrap";
import { useGlobalActions, useGlobalState } from "../context/globalContext";
import Node from "./node";

const groundInitial = Array(100).fill(Array(100).fill({}));
groundInitial.forEach((row, i) => {
  row.forEach((cell, j) => {
    groundInitial[i][j] = createRef();

    
      const initialCreature = {
        row: i,
        col: j,
        birth: Date.now(),
        color: "#2BFF00",
      }
      groundInitial[i][j].current = Math.random()>0.7?initialCreature:null;
    
  });
});
const Screen = ()=>{

    
    const {ground} = useGlobalState();
    const [trueRef,setTrueRef] = useState(null);
    const screenRef = useRef(null);
    
  
    




    
    

   
    
    useEffect(()=>{
        if(screenRef){
            if(screenRef.current){
                setTrueRef(screenRef.current);
            }
        }
    },[screenRef])


    useEffect(()=>{
        
        // setCreaturesData([...creatures])
        // console.log(creatures);
    },[ground])

    // useEffect(()=>{
    //     console.log(creaturesData);
    // },[creaturesData])

    console.log(ground)



    return (
      <div
        style={{ height: "100%", width: "100%", backgroundColor: "#1E1E1E" }}
        ref={screenRef}
      >
        {(trueRef)
          ? groundInitial.map((m,i) => {
              return (
                <Row style={{padding:0,margin:0,width:"100%",height:20}}>
                    {m.map((cell,j)=>{
                        return (
                          <Col style={{ padding: 0, margin: 0 }}>
                            {cell.current ? (
                              <Card
                                style={{
                                  border: "none",
                                  borderRadius: 0,
                                  padding: 0,
                                  margin: 0,
                                  height:"100%",
                                  width:"100%",
                                  backgroundColor: cell.current.color,
                                }}
                              ></Card>
                            ) : null}
                          </Col>
                        );
                    })}
                </Row>
              )}):null}
      </div>
    );
}

export default Screen;
