import React, { createRef, useEffect, useState ,useRef} from "react";



const GlobalStateContext = React.createContext(undefined);
const GlobalActionsContext = React.createContext(undefined);


const bounds = {w:window.innerWidth-20,h:window.innerHeight-20,l:10,t:10}
// bounds = {
//   l: screenRef.current.offsetLeft,
//   t: screenRef.current.offsetTop,
//   w: screenRef.current.clientWidth,
//   h: screenRef.current.clientHeight,
// };

const useInterval = (callback, delay) => {
  const savedCallback = useRef();

  // Remember the latest callback.
  useEffect(() => {
    savedCallback.current = callback;
  });
  // Set up the interval.
  useEffect(() => {
    function tick() {
      if (typeof savedCallback?.current !== "undefined") {
        savedCallback?.current();
      }
    }
    if (delay !== null) {
      const id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
};



const creaturesInit = Array(100).fill({});
// console.log(bounds)
creaturesInit.forEach((val, index) => {
  creaturesInit[index] = createRef();
  creaturesInit[index].current = {
    x: Math.floor(Math.random() * bounds.w),
    y: Math.floor(Math.random() * bounds.h),
    birth:Date.now(),
    color:"#A089FF",
    age:Math.random()*5000
  };
});
const dead = [];
const prevDead=[];
const ga = function*(){


  // console.log(creatures)

  for (var i = 0; i < 100; i++) {
    console.log(dead);
    prevDead.forEach((v)=>{
      creaturesInit.splice(v, 1);
    })
    prevDead.splice(0, prevDead.length);
    dead.forEach((v)=>{
      prevDead.push(v);
    })

    dead.splice(0,dead.length);
    const l = creaturesInit.length;
    for (var j = 0; j < l; j++) {
      var prob = Math.random();
      if (prob>0.97) {
        dead.push(j);
        creaturesInit[j].current.color="#FF0000";

      }else{
        const pos = [1, -1];
        const skipX = Math.floor(Math.random() * 50);
        const skipY = Math.floor(Math.random() * 50);

        const plusMinusX = Math.floor(Math.random() * 2);
        const plusMinusY = Math.floor(Math.random() * 2);

        const newPosition = {
          x: creaturesInit[j].current.x + pos[plusMinusX] * skipX,
          y: creaturesInit[j].current.y + pos[plusMinusY] * skipY,
        };
        if (newPosition.x > bounds.w + bounds.l) {
          newPosition.x = bounds.w;
        }
        if (newPosition.y > bounds.h + bounds.t) {
          newPosition.y = bounds.h;
        }
        if (newPosition.x < bounds.l) {
          newPosition.x = bounds.l;
        }
        if (newPosition.y < bounds.t) {
          newPosition.y = bounds.t;
        }

        creaturesInit[j].current = newPosition;
      }


    }




    for (var i = 0; i < l; i++) {
      // console.log(creaturesInit[i].current.x);
      if (dead.indexOf(i) === -1){
        for (var j = 0; j < l; j++) {
          if (
            Math.floor(
              Math.abs(creaturesInit[i].current.x - creaturesInit[j].current.x)
            ) < 20 &&
            Math.floor(
              Math.abs(creaturesInit[i].current.y - creaturesInit[j].current.y)
            ) < 20 &&
            i !== j &&
            dead.indexOf(j) === -1
          ) {
            creaturesInit[i].current.color = "#FFFFFF";
            const child = createRef();
            const newChildData = {
              x: (creaturesInit[i].current.x + creaturesInit[j].current.x) / 2,
              y: (creaturesInit[i].current.y + creaturesInit[j].current.y) / 2,
              color: "#22FF00",
              birth: Date.now(),
              age: Math.random() * 5000,
            };
            child.current = newChildData;

            var prob = Math.random();
            if (prob > 0.95) {
              creaturesInit.push(child);
            }

            break;
          } else {
            creaturesInit[i].current.color = "#A089FF";
          }
        }
      }

    }

    yield{creatures:creaturesInit,dead:[...dead]}
    // console.log(creaturesInit);
  }
}

const GlobalContextProvider = ({ children }) => {



  const [creatures, setCreatures] = useState(Array.from(Array(100).keys()));

  const [dead,setDead] = useState([]);
  const [prevDead, setPrevDead] = useState([]);
  const gaProcessSteps = ga();

  useEffect(()=>{
    console.log(gaProcessSteps)
  },gaProcessSteps)






  useInterval(() => {
    // console.log(gaProcessSteps)

    if (!gaProcessSteps) return;
    const next = gaProcessSteps.next();
    // console.log(next);
    if (next.done) return ;
    const creatureState = next.value;
    // console.log(creatureState.creatures);
    setPrevDead([...dead]);
    setCreatures([...creatureState.creatures]);
    setDead([...creatureState.dead]);
  }, 1000);

  return (
    <GlobalStateContext.Provider
      value={{
        creatures,
        dead,
        prevDead
      }}

    >
      <GlobalActionsContext.Provider value={{ setCreatures }}>
        {children}
      </GlobalActionsContext.Provider>
    </GlobalStateContext.Provider>
  );
};

const useGlobalState = () => {
  const context = React.useContext(GlobalStateContext);
  if (context === undefined) {
    throw new Error(
      "useGlobalState error"
    );
  }

  return context;
};

const useGlobalActions = () => {
  const context = React.useContext(GlobalActionsContext);
  if (context === undefined) {
    throw new Error(
      "useGlobalActions error"
    );
  }

  return context;
};

export { useGlobalState, useGlobalActions, GlobalContextProvider };
