import React, { useRef, useEffect, useState, useCallback } from "react";
import mojs from "@mojs/core";
const EarthQuake = ({ duration, e }) => {
  const animDom = useRef();
  const bouncyCircle = useRef();
  const [isOpen, setIsOpen] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    // Prevent multiple instansiations on hot reloads
    if (bouncyCircle.current) return;

    // Assign a Shape animation to a ref
    bouncyCircle.current = new mojs.Shape({
      parent: animDom.current,
      shape: "circle",
      fill: "none",
      radius: 25,
      strokeWidth: { 50: 0 },
      scale: { 0: 1 },
      angle: { "rand(-35, -70)": 0 },
      duration: duration,
      easing: "cubic.out",

      radius: { 0: 15 },
      strokeWidth: { 30: 0 },
      stroke: "magenta",
      delay: "rand(75, 150)",
    });
  });

  // Update the animation values when the prop changes
  useEffect(() => {
    bouncyCircle.current.tune({ x: e.offsetX, y: e.offsetY }).replay();
  });

  return <div ref={animDom} className="MojsExample"></div>;
};

export default EarthQuake;
