import { createRef, useEffect, useRef, useState } from "react";
import { Card ,Row,Col} from "react-bootstrap";
import { useGlobalActions, useGlobalState } from "../context/globalContext";
import Node from "./node";

const Screen = ()=>{

    
    const {ground} = useGlobalState();
    const [trueRef,setTrueRef] = useState(null);
    const screenRef = useRef(null);
    
  
    




    
    

   
    
    useEffect(()=>{
        if(screenRef){
            if(screenRef.current){
                setTrueRef(screenRef.current);
            }
        }
    },[screenRef])


    useEffect(()=>{
        
        // setCreaturesData([...creatures])
        // console.log(creatures);
    },[ground])

    // useEffect(()=>{
    //     console.log(creaturesData);
    // },[creaturesData])




    return (
      <div
        style={{ height: "100%", width: "100%", backgroundColor: "#1E1E1E" }}
        ref={screenRef}
      >
        {(trueRef)
          ? ground.map((m,i) => {
              return (
                <Row style={{padding:0,margin:0,width:"100%"}}>
                    {m.map((cell,j)=>{
                        return(<Col style={{padding:0,margin:0}}>
                            {!isNaN(ground[i][j])?<Card style={{border:"none",borderRadius:0,padding:0,margin:0,backgroundColor:cell.color}}></Card>:null}
                        </Col>)
                    })}
                </Row>
              )}):null}
      </div>
    );
}

export default Screen;
