import React, { useRef, useEffect, useState } from "react";
import Tree from "react-d3-tree";
import "./home.css";
import ReactTooltip from "react-tooltip";
import Switch from "react-switch";
// This is a simplified example of an org chart with a depth of 2.
// Note how deeper levels are defined recursively via the `children` property.
import treeData from "./data/tree";
import { Col, Container, Row, Card, Button } from "react-bootstrap";

import { BsFillGrid3X3GapFill, BsList, BsX } from "react-icons/bs";
import { findRenderedDOMComponentWithClass } from "react-dom/cjs/react-dom-test-utils.development";
const Preferences = (props) => {
  const [isPreferencesVis, setIsPreferencesOn] = useState(false);
  const [disappearingToolTip, setDisappearingToolTip] = useState(
    props.disappearingToolTip
  );
  return (
    <div
      style={{
        right: 0,
        top: 0,
        margin: 12,
        // height: "40vh",
        // width: "20vw",
        position: "absolute",
        display: "block",
        // backgroundColor: "white",
      }}
    >
      {isPreferencesVis ? (
        <Card
          style={{
            margin: 8,
            padding: 4,
            // height: "30vh",
            width: "15vw",
            fontSize: 10,
            fontWeight: "500",
            color: "white",
            backgroundColor: "#4f4f4f",
          }}
          //   onMouseOut={() => {
          //     setIsPreferencesOn(false);
          //   }}
        >
          <Row style={{ padding: 0, margin: 0 }}>
            <Col style={{ padding: 0, margin: 0 }}>
              <BsX
                style={{ float: "right", fontSize: 15 }}
                onClick={() => {
                  setIsPreferencesOn(false);
                }}
              ></BsX>
            </Col>
          </Row>
          <Row style={{ padding: 2, margin: 2 }}>
            <Col style={{ padding: 0, margin: 0 }}>
              <span style={{ padding: 0, margin: 0, float: "left" }}>
                Disappearing tooltip
              </span>
            </Col>
            <Col style={{ padding: 0, margin: 0, textAlign: "right" }}>
              <Switch
                onColor="#ff8d29"
                activeBoxShadow=""
                height={14}
                width={25}
                checked={disappearingToolTip}
                onChange={(e) => {
                  console.log(e);
                  setDisappearingToolTip(e);
                  props.setDisappearingToolTip(e);
                }}
              ></Switch>
            </Col>
          </Row>
          <Row
            style={{ padding: 1, margin: 0, backgroundColor: "#717171" }}
          ></Row>
        </Card>
      ) : (
        <BsList
          onClick={() => {
            setIsPreferencesOn(true);
          }}
          style={{
            margin: 8,
            color: "white",
            fontSize: 20,
          }}
        ></BsList>
      )}
    </div>
  );
};
const Tooltip = (props) => {
  return (
    <Card
      style={{
        left: 0,
        bottom: 0,
        margin: 12,
        height: "60vh",
        width: "15vw",
        position: "absolute",
        display: "block",
        backgroundColor: "white",
      }}
    ></Card>
  );
};
export default function OrgChartTree() {
  const treeRef = useRef(null);
  const [translation, setTranslation] = useState({ x: 0, y: 0 });
  const [depth, setDepth] = useState(0);
  const [scaleExtent, setScaleExtent] = useState({ min: 0.1, max: 1 });
  const [tooltip, setTooltip] = useState({});
  const [isToolTipVis, setIsToolTipVis] = useState(false);
  const [disappearingToolTip, setDisappearingToolTip] = useState(false);

  useEffect(() => {
    console.log(treeRef);
    setTranslation({
      y: Math.floor(treeRef.current.clientHeight / 2),
      x: Math.floor(treeRef.current.clientWidth / 2),
    });
  }, [treeRef]);

  useEffect(() => {
    console.log(depth);
    if (depth > 0) {
      var temp_y = translation.y;
      temp_y = Math.floor(temp_y - temp_y / 7);
      setTranslation({ x: translation.x, y: temp_y });

      var temp_max = scaleExtent.max;
      temp_max = temp_max - 0.08;
      setScaleExtent({ min: scaleExtent.min, max: temp_max });
    }
  }, [depth]);
  return (
    // `<Tree />` will fill width/height of its container; in this case `#treeWrapper`.
    <div
      id="treeWrapper"
      ref={treeRef}
      style={{
        width: "100%",
        height: "100%",
        padding: 0,
        margin: 0,

        backgroundColor: "black",
      }}
    >
      <Preferences
        setDisappearingToolTip={setDisappearingToolTip}
        disappearingToolTip={disappearingToolTip}
      ></Preferences>
      {disappearingToolTip ? (
        isToolTipVis ? (
          <Tooltip></Tooltip>
        ) : null
      ) : (
        <Tooltip></Tooltip>
      )}
      <Tree
        data={treeData}
        enableLegacyTransitions={true}
        orientation="vertical"
        transitionDuration={300}
        rootNodeClassName="node__root"
        branchNodeClassName="node__branch"
        leafNodeClassName="node__leaf"
        initialDepth={0}
        translate={translation}
        collapsible={true}
        zoomable={true}
        zoom={1}
        scaleExtent={scaleExtent}
        separation={{ siblings: 1, nonSiblings: 1 }}
        shouldCollapseNeighborNodes={false}
        onNodeClick={(...args) => {
          console.log(args);
          if (args[0].__rd3t.depth > depth) setDepth(args[0].__rd3t.depth);
        }}
        onNodeMouseOver={(...args) => {
          if (isToolTipVis === false) {
            setIsToolTipVis(true);
          }
        }}
        onNodeMouseOut={(...args) => {
          setIsToolTipVis(false);
        }}
      />
    </div>
  );
}
