import { createRef, useEffect, useRef, useState } from "react";
import { Card } from "react-bootstrap";
import { useGlobalActions, useGlobalState } from "../context/globalContext";
import Node from "./node";

const Screen = ()=>{

    
    const {creatures,creatureRef} = useGlobalState();
    const {setCreatures} = useGlobalActions();
    const [trueRef,setTrueRef] = useState(null);
    const screenRef = useRef(null);
    const nodeRef = creatures.map(()=>createRef());

    const cre = Array(100).fill({});
    const [colorNode, setColorNode] = useState(Array(100).fill("#34DAFF"));

    function checkTouch(){
        for(var i=0;i<100;i++){
            for(var j=0;j<100;j++){
                if((cre[i].x<cre[j].x+5||cre[i].x>cre[j].x-5)&&(cre[i].y<cre[j].y+5||cre[i].y>cre[j].y-5)){
                    colorNode[i] = "#FF3434";
                    colorNode[j] = "#FF3434";
                }else{
                    colorNode[i] = "#34DAFF";
                    colorNode[j] = "#34DAFF";
                }
            }
        }
        setColorNode([...colorNode])
    }
    useEffect(()=>{
        nodeRef.forEach((ref,index)=>{
            const handler = (e) => {
              if (e?.detail) {
                const newC1 = e.detail;
                cre[index] = newC1;
                checkTouch();
              }
            };
            ref?.current?.addEventListener("position", handler);
            return () =>
              ref?.current?.removeEventListener("position", handler);
        })
        
    },[nodeRef])

    
    

   
    
    useEffect(()=>{
        if(screenRef){
            if(screenRef.current){
                setTrueRef(screenRef.current);
            }
        }
    },[screenRef])




    return (
      <div
        style={{ height: "100%", width: "100%", backgroundColor: "#1E1E1E" }}
        ref={screenRef}
      >
        {trueRef
          ? creatures.map((m,index) => {
              return (
                <Node
                    color = {colorNode[index]}
                    key ={index}
                    id={index}
                    childRef = {nodeRef[index]}
                  bounds={{
                    l: screenRef.current.offsetLeft,
                    t: screenRef.current.offsetTop,
                    w: screenRef.current.clientWidth,
                    h: screenRef.current.clientHeight,
                  }}
                >
                  <span key={index} ref = {nodeRef[index]} id={index}></span>
                </Node>
              );
            })
          : null}
      </div>
    );
}

export default Screen;
