import { createRef, useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
const demo = Array.from(Array(20).keys());

const Creature = (props) => {
  return (
    <div
      key={props.creature.uid}
      style={{
        borderRadius: 5,
        height: 10,
        width: 10,
        backgroundColor: "#111111",
        position: "absolute",
        top: props.creature.top,
        left: props.creature.left,
        transition: "all 1sec linear",
      }}
    ></div>
  );
};
const Earth = () => {
  const [creatures, setCreatures] = useState([]);
  const [earthDimensions, setEarthDimensions] = useState(null);
  const earthRef = createRef();

  function addNewCreature() {
    const creaturesTemp = creatures;
    creaturesTemp.push({
      uid: uuidv4(),
      top: Math.floor(Math.random() * earthDimensions.h + earthDimensions.top),
      left: Math.floor(
        Math.random() * earthDimensions.w + earthDimensions.left
      ),
    });
    setCreatures([...creaturesTemp]);
  }
  useEffect(() => {
    console.log({ ...earthRef });
    if (earthRef && earthRef.current && !earthDimensions) {
      setEarthDimensions({
        top: earthRef.current.offsetTop,
        left: earthRef.current.offsetLeft,
        w: earthRef.current.offsetWidth,
        h: earthRef.current.offsetHeight,
      });
    }
  }, [earthRef]);
  return (
    <div
      ref={earthRef}
      style={{ backgroundColor: "#2C931D", height: "100%", width: "100%" }}
      onClick={(e) => {
        // console.log(onClick);
        e.preventDefault();
        addNewCreature();
      }}
    >
      {earthRef && earthDimensions
        ? creatures.map((creature) => {
            return <Creature creature={creature}></Creature>;
          })
        : null}
    </div>
  );
};

export default Earth;
