import { useEffect, useState } from "react";
import {Card} from "react-bootstrap";
const Node = (props)=>{

    const bounds = props.bounds;
    const size = 10;
    const [position, setPosition] = useState({
      x: Math.floor(Math.random() * bounds.w)-size/2,
      y: Math.floor(Math.random() * bounds.h)-size/2,
    });

    useEffect(()=>{
        setInterval(()=>{
            const pos = [1,-1]
            const skipX = Math.floor(Math.random()*3);
            const skipY = Math.floor(Math.random() * 3);

            const plusMinusX = Math.floor(Math.random()*2);
            const plusMinusY = Math.floor(Math.random() * 2);

            const newPosition = {
              x: position.x + pos[plusMinusX] * skipX,
              y: position.y + pos[plusMinusY] * skipY,
            };

            setPosition({...newPosition});

        },1000)
    },[position])
    return (
      <Card
        style={{
          backgroundColor: "#34FF3B",
          height: size,
          width: size,
          top:position.y,
          left:position.x,
          position:"absolute",
          borderRadius: size/2,
          border: "none",
        }}
      >
        {props.children}
      </Card>
    );
}

export default Node;
