import { createRef, useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";
const demo = Array.from(Array(20).keys());

const Creature = (props) => {
  const earthDimensions = props.dim;
  console.log(earthDimensions);
  const size = 10;
  const [position, setPosition] = useState({
    x: Math.random(
      earthDimensions.w * Math.random() + earthDimensions.w - size / 2
    ),
    y: Math.random(
      earthDimensions.h * Math.random() + earthDimensions.h - size / 2
    ),
  });

  function changePosition() {
    const pos = [1, -1];
    const skipX = Math.floor(Math.random() * 50 + 20);
    const skipY = Math.floor(Math.random() * 50 + 20);

    const plusMinusX = Math.floor(Math.random() * 2);
    const plusMinusY = Math.floor(Math.random() * 2);

    const newPosition = {
      x: position.x + pos[plusMinusX] * skipX,
      y: position.y + pos[plusMinusY] * skipY,
    };
    if (newPosition.x + size > earthDimensions.w + earthDimensions.left) {
      newPosition.x = earthDimensions.w + earthDimensions.left - size;
    }
    if (newPosition.y + size > earthDimensions.h + earthDimensions.top) {
      newPosition.y = earthDimensions.h + earthDimensions.top - size;
    }
    if (newPosition.x < earthDimensions.left) {
      newPosition.x = earthDimensions.left;
    }
    if (newPosition.y < earthDimensions.top) {
      newPosition.y = earthDimensions.top;
    }

    setPosition({ ...newPosition });
  }

  useEffect(() => {
    const interval = setInterval(() => {
      changePosition();
      //   console.log(position);
    }, Math.floor(Math.random() * 20) + 10);
    return () => {
      clearInterval(interval);
    };
  }, [position]);
  return (
    <div
      style={{
        borderRadius: 5,
        height: 10,
        width: 10,
        backgroundColor: "#111111",
        position: "absolute",
        top: position.x,
        left: position.y,
        transition: "all 1s ease-in-out",
      }}
    ></div>
  );
};
const Earth = () => {
  const [creatures, setCreatures] = useState([]);
  const [earthDimensions, setEarthDimensions] = useState(null);
  const earthRef = createRef();

  function addNewCreature() {
    const creaturesTemp = creatures;
    creaturesTemp.push({
      uid: uuidv4(),
    });
    setCreatures([...creaturesTemp]);
  }
  useEffect(() => {
    console.log({ ...earthRef });
    if (earthRef && earthRef.current && !earthDimensions) {
      setEarthDimensions({
        top: earthRef.current.offsetTop,
        left: earthRef.current.offsetLeft,
        w: earthRef.current.offsetWidth,
        h: earthRef.current.offsetHeight,
      });
      for (var i = 0; i < 20; i++) {
        addNewCreature();
      }
    }
  }, [earthRef]);
  return (
    <div
      ref={earthRef}
      style={{ backgroundColor: "#2C931D", height: "100%", width: "100%" }}
      onClick={(e) => {
        // console.log(onClick);
        e.preventDefault();
        addNewCreature();
      }}
    >
      {earthRef && earthDimensions
        ? creatures.map((creature) => {
            return (
              <Creature key={creature.uid} dim={earthDimensions}></Creature>
            );
          })
        : null}
    </div>
  );
};

export default Earth;
