import { useEffect, useState ,useRef} from "react";
import {Card} from "react-bootstrap";
import { useGlobalActions, useGlobalState } from "../context/globalContext";
import "./node.css";
const Node = (props)=>{

    const bounds = props.bounds;
    const size = 10;
    
    
    const nodeRef = useRef(null);
    const [position, setPosition] = useState(props.position);
    const [color,setColor] = useState("#37F5FF");

    useEffect(()=>{
        if(props.current){
            setPosition({ x: props.current.x, y: props.current.y });
            setColor(props.current.color);
        }
        
    },[props])
    


    return (
      position?<Card
        ref={nodeRef}
        style={{
          backgroundColor: props.color,
          height: size,
          width: size,
          top: position.y,
          left: position.x,
          position: "absolute",
          borderRadius: size / 2,
          border: "none",
          transition: "all 2s ease-in-out",
        }}
      >
        {props.children}
      </Card>:null
    );
}

export default Node;
