import { createRef, useEffect, useRef, useState } from "react";
import { Card } from "react-bootstrap";
import { v4 as uuidv4 } from "uuid";
const demo = Array.from(Array(20).keys());

const Creature = (props) => {
  const earthDimensions = props.dim;
  //   console.log(earthDimensions);
  const creatureRef = useRef(null);
  const size = 10;
  const [color, setColor] = useState(props.color ? props.color : "#0000000");
  const [position, setPosition] = useState({
    x: Math.floor(props.dim.w * Math.random() + props.dim.left - size / 2),
    y: Math.floor(props.dim.h * Math.random() + props.dim.top - size / 2),
  });

  function changePosition() {
    const pos = [1, -1];
    const skipX = Math.floor(Math.random() * 20);
    const skipY = Math.floor(Math.random() * 20);

    const plusMinusX = Math.floor(Math.random() * 2);
    const plusMinusY = Math.floor(Math.random() * 2);

    const newPosition = {
      x: position.x + pos[plusMinusX] * skipX,
      y: position.y + pos[plusMinusY] * skipY,
    };

    if (newPosition.x + size > props.dim.w + props.dim.left) {
      newPosition.x = props.dim.w + props.dim.left - size;
    }
    if (newPosition.y + size > props.dim.h + props.dim.top) {
      newPosition.y = props.dim.h + props.dim.top - size;
    }
    if (newPosition.x < props.dim.left) {
      newPosition.x = props.dim.left;
    }
    if (newPosition.y < props.dim.top) {
      newPosition.y = props.dim.top;
    }

    setPosition(newPosition);
  }

  function getNeighbourHood() {
    const neighbours = [];
    if (creatureRef?.current) {
      const children = creatureRef.current?.parentNode?.childNodes;
      if (children) {
        Object.keys(children).forEach((key) => {
          if (
            children[key]?.nodeName === "DIV" &&
            Math.abs(children[key].offsetLeft - position.x) < 10 &&
            Math.abs(children[key].offsetTop - position.y) < 10 &&
            children[key].id.localeCompare(props.uid) !== 0
          ) {
            neighbours.push(children[key]);
          }
        });
      }
    }

    return { neighbours };
  }

  useEffect(() => {
    // console.log(props.uid);
    const interval = setInterval(() => {
      changePosition();
      const { neighbours } = getNeighbourHood();
      //   console.log(neighbours);

      if (neighbours.length > 0) {
        const event = new CustomEvent("birth", {
          detail: { mother: props.uid, candidates: neighbours },
        });
        props.earthRef?.current?.dispatchEvent(event);
      }
    }, Math.floor(Math.random() * 5000) + 10);
    return () => {
      clearInterval(interval);
    };
  }, [position]);
  return (
    <div
      ref={creatureRef}
      id={props.uid}
      style={{
        borderRadius: 5,
        height: 10,
        width: 10,
        backgroundColor: props.color,
        position: "absolute",
        top: position.y,
        left: position.x,
        transition: "all 0.5s ease-in-out",
      }}
    ></div>
  );
};

const Earth = () => {
  const [creatures, setCreatures] = useState([]);
  const [earthDimensions, setEarthDimensions] = useState(null);
  const earthRef = createRef();

  useEffect(() => {
    if (earthRef && earthRef.current && !earthDimensions) {
      const handler = (e) => {
        if (e.detail) {
          console.log(e.detail);
          e.detail.candidates.forEach(() => {
            addNewCreature({ color: "#FF0000" });
          });
        }
      };
      earthRef.current.addEventListener("birth", handler);
      return () => earthRef?.current?.removeEventListener("birth", handler);
    }
  }, [earthRef, earthRef.current]);

  function addNewCreature(props) {
    const creaturesTemp = creatures;
    creaturesTemp.push({
      uid: uuidv4(),
      color: props?.color ? props.color : "#000000",
    });
    // console.log(creaturesTemp);
    setCreatures([...creaturesTemp]);
  }

  function removeRandom() {
    const creaturesTemp = creatures;
    creaturesTemp.splice(Math.floor(Math.random() * creatures.length), 1);
    setCreatures([...creaturesTemp]);
  }
  useEffect(() => {
    if (earthRef && earthRef.current && !earthDimensions) {
      setEarthDimensions({
        top: earthRef.current.offsetTop,
        left: earthRef.current.offsetLeft,
        w: earthRef.current.offsetWidth,
        h: earthRef.current.offsetHeight,
      });
      for (var i = 0; i < 20; i++) {
        addNewCreature();
      }
    }
  }, [earthRef]);
  return (
    <div
      ref={earthRef}
      style={{ backgroundColor: "#2C931D", height: "100%", width: "100%" }}
      onMouseDown={(e) => {
        // console.log(onClick);
        e.preventDefault();
        if (e.button === 0) {
          addNewCreature();
        } else {
          removeRandom();
        }
      }}
    >
      {earthRef && earthDimensions
        ? creatures.map((creature) => {
            return (
              <Creature
                color={creature.color}
                key={creature.uid}
                uid={creature.uid}
                earthRef={earthRef}
                dim={earthDimensions}
              ></Creature>
            );
          })
        : null}
    </div>
  );
};

export default Earth;
